{
    "metadata": {
        "name": "example-env",
        "displayName": "JService Demo Environment",
        "description": "This environment demos API-M using the JService API",
        "tags": ["jservice", "tag example", "example-env"]
    }
}

{
    "metadata": {
        "name": "jservice-app",
        "displayName": "JService Application",
        "description": "REST API to query Jeopardy Questions"
    }
}

{
    "metadata": {
        "name": "prod-env",
        "displayName": "Prod Demo Environment",
        "description": "This environment is for Production APIs",
        "tags": ["prod", "restcountries"]
    }
}

{
    "metadata": {
        "name": "rc-app",
        "displayName": "REST Countries Application",
        "description": "Logical Collection of Country Information"
    }
}

{
  "metadata": {
    "name": "rc-devport",
    "description": "Development Portal for the REST Countries API",
    "tags": [],
    "kind": "devportal"
  },
  "desiredState": {
    "ingress": {
      "gatewayRefs": [
        {
          "ref": "/services/environments/prod-env/gateways/devport-rc-gw"
        }
      ]
    },
    "devPortalTheme": {
      "customConfig": {
        "fonts": {
          "assignments": {
            "headings": {
              "kind": "google-web-font",
              "value": "Lato"
            },
            "body": {
              "kind": "google-web-font",
              "value": "Fira+Sans"
            },
            "code": {
              "kind": "google-web-font",
              "value": "IBM+Plex+Mono"
            },
            "cta": {
              "kind": "google-web-font",
              "value": "Lato"
            },
            "special": {
              "kind": "google-web-font",
              "value": "Lato"
            }
          }
        },
        "primary": {
          "color": {
            "primary": "#3b43be",
            "accent": "#3b43be",
            "fill": "#ffffff",
            "ink": "#171d21"
          }
        },
        "secondary": {
          "color": {
            "primary": "#3b43be",
            "accent": "#ffffff",
            "fill": "#f7f8fa",
            "ink": "#2c3039",
            "gray": "#272938"
          }
        }
      },
      "brandName": "REST Countries Inc.",
      "logo": "data:image/png;base64,"
    },
    "publishedApiRefs": [],
    "devPortalType": "private"
  }
}
